"""
Comedy, action, drama, horror and thriller seem to be the only independent genres
"""

lines = open('movie_titles_metadata.txt', encoding = 'utf-8', errors = 'ignore').read().split('\n')
genres = []
titles = []
md = {}
movid = []
for line in lines[:-1]:
    _line = line.split(' +++$+++ ')
    genres.append(_line[-1])
    genre = _line[-1].split(',')
    md[_line[1]] = {_line[0], _line[-1]}
    #print(_line[1])
    # for p in _line[-1].split():
    #     p = p.strip("").strip('[').strip(']')
    #     if p == "'crime'":
    #         print(_line[1])
    #print('\n')
    #print(_line[0], _line[1], _line[-1])
    if len(genre) == 1:
        p = genre[0].strip("").strip('[').strip(']')
        if p == "'horror'":
            movid.append(_line[0])
    titles.append(_line[1])
#print(movid)
#print(md)
# for i in sorted(titles):
#     print(i)
cleanedgenres = []

#print(md)
for genre in genres:
    genre = genre.split(',')
    glist = []
    for g in genre:
        g = g.strip('[').strip(']')
        g = g.strip()
        g = g.replace("'", "")
        g = g.replace('"', "")
        glist.append(g)
    cleanedgenres.append(glist)

